import { PureComponent} from "react";
import './header.css';
import SwitchToDarkIcon from './icon-moon.svg';
import SwitchToLightIcon from './icon-sun.svg';
class Header extends PureComponent {

 Header = ({ themeLight, setThemeLight }) => {
  const switchThemeIcon = themeLight ? SwitchToDarkIcon : SwitchToLightIcon;

  const changeTheme = () => {
    setThemeLight(!themeLight);
  };

  return (
    <header>
      <h1>TODO</h1>
      <button className="btn switch-theme-btn" onClick={changeTheme}>
        <img src={switchThemeIcon} alt="Dark Theme" />
      </button>
    </header>
  );
};
}

export default Header;
