import React, { useState, useEffect} from "react";
import "./todoform.css";
export default function Todoapp(){

  const[data,setDataItem] = useState([]);
  const[value,setValue] = useState();
  console.log(data);


  const setData = (event) => {
    setValue(event.target.value);
  }

  const add = (e)=> {
    e.preventDefault();
    if(!value || value.trim().length==0){
      alert("enter something")
    }else{
    setDataItem([...data,{text:value,completed: false,id:new Date().getTime()},]);
    }
    setValue("");
  };

  useEffect(() => {
    const todos=JSON.parse(localStorage.getItem("key"));
    if(todos){
      setDataItem(todos);
    }else{
      return [];
    }
  }, [])

  useEffect(() => {
   localStorage.setItem("key",JSON.stringify(data));
  }, [data])

  return(
    <div className="container">
      <div className="todo_list">
        <div className="todo_title">
          <span>TODO</span>
          <span>
          <img src="images/icon-moon.svg" alt="moonimg"/>
          </span>
        </div>
        <div className="input_field">
          <form onSubmit={add}>
            <input type="text" placeholder="Create a new todo.." value={value} onChange={setData}/>
          </form>
        </div>
        <div className="todos_list">
          data.map((val,i)=>{

          })
        </div>
      </div>
    </div>
  )
}
