import React, {useEffect,useState} from "react";
import "./todoapp.css";
// import setLightImage from "./icon-sun.svg";
// import setDarkImage from "./icon-moon.svg";
export default function Todoapp(value) {

   const todo=document.createElement("div");
   const todoText=document.createElement("p");
   const todoCheckBox=document.createElement("input");
   const todoCheckBoxLabel=document.createElement("label");
//    const todoCross=document.createElement("span");

   todoText.textContent= value;
   todoCheckBox.type="checkbox";
   todoCheckBox.name="checkbox";
   todoCheckBoxLabel.htmlFor="checkbox";

   todoCheckBoxLabel.addEventListener("click", function (e){
       if(todoCheckBox.checked){
           todoCheckBox.checked=false;
           todoText.style.textDecoration="none";
           todoCheckBoxLabel.classList.remove("active");
        //    updateTodos(value,false);
        //    countComplted();
       } else {
        // updateTodos(value,true);
        // countComplted();
        todoCheckBox.checked=true;
           todoText.style.textDecoration="line-through";
           todoCheckBoxLabel.classList.add("active");
       }
   });


   todo.appendChild(todoCheckBox);
   todo.appendChild(todoCheckBoxLabel);
   todo.appendChild(todoText);


  return (

    <div className="top-img">
    <div className="main">
    <div className="header">
        <h1>TODO</h1>
        <div onClick="changeTheme()" className="tgl-btn">
        </div>    
    </div>
    <div className="type-todo">
        <div></div>
        <input type="text" id="todo-input"/>
    </div>
    <div className="todos"></div>
    <div classList="remarks">
        <div className="completedCount small-size">
        </div>
        <div className="small-size" onclick="clearCompleted()">Clear Completed</div>
        <div className="filters">
            <div className="filtersActive" onclick="showAll()">All</div>
            <div onclick="filtersActive()">Active</div>
            <div onclick="filterCompleted()">Completed</div>
        </div>
    </div>
  </div>
  </div>
  )  






}
